mapper.select:
SELECT b.msg_type ,b.input_code ,t.* FROM wxcms_msg_text t , wxcms_msg_base b
WHERE t.base_id = b.id

	and b.input_code=#{inputcode}


	and account = #{account}

ORDER BY b.create_time DESC

SELECT b.msg_type ,b.input_code ,t.* FROM wxcms_msg_text t , wxcms_msg_base b
WHERE t.base_id = b.id

	and b.input_code=#{inputcode}


	and account = #{account}

ORDER BY b.create_time DESC

mapper.delete:
DELETE FROM wxcms_msg_text


		ID = #{id}


		and base_id = #{baseId}
